package javapractice_loop;

public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
            System.out.println("Coding Everybody " + i);
        }
		
		int k = 0;
		while(k<10){
		    System.out.println("coding everybody"+k);
		    k++;
		}
	}

}
